import React from 'react'
import './NavBar.css'
import { useNavigate,Link} from 'react-router-dom'
import useUserStore from '../../../store/user';

const NavBar = () => {
    const navigate = useNavigate()
    const { token, user } = useUserStore(state => ({ token: state.token, user: state.user })); // Get token & user details


    return (
        <header>
            <div className="logo" onClick={() => {
                navigate('/')
            }}>
                <ion-icon name="game-controller"></ion-icon>
                <div style={{display:"block"}}>
                <p>The Game Zone</p>
                {token ? (
                            <span className="username">Hey, {user?.username} ðŸ‘‹</span>
                        ) : (
                            <Link to="/login" className="login-link">Click Here to Login</Link>
                        )}
                </div>

            </div>
            <button onClick={() => {
                navigate('/leaderboard')
            }}
                type="button" id="btn">LEADERBOARD</button>
        </header>
    )
}

export default NavBar
