import React from 'react'
import Hero from './Hero/Hero'
import NavBar from './NavBar/NavBar'
import './Main.css'
import bg from '../../assets/home/bg.png'
import Footer from './Footer/Footer'
import Games from './Games/Games'

import { useEffect } from 'react';
import useUserStore from '../../../store/user';

const Main = () => {
    const { token, user, setUser } = useUserStore(state => ({
        token: state.token,
        user: state.user,
        setUser: state.setUser
    }));

    // Fetch user details from the server if logged in
    useEffect(() => {
        const fetchUser = async () => {
            if (!token) return;
            try {
                const res = await fetch(`${process.env.REACT_APP_BASE_URL}/user-detail`, {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });

                if (res.ok) {
                    const data = await res.json();
                    setUser(data, token); // Store user details in Zustand
                } else {
                    console.error("Failed to fetch user details");
                }
            } catch (error) {
                console.error("Error fetching user details:", error);
            }
        };

        fetchUser();
    }, [token, setUser]);

    return (
        <>
            <div className='container'
                style={{ background: `linear-gradient(rgba(0,0,0,0.5),rgba(0,0,0,0.5)), url(${bg})` }}>
                <NavBar />
                {token ? (
                            <span className="username">Hey, {user.name} ðŸ‘‹</span>
                        ) : (
                            <Link to="/login" className="login-link">Click Here to Login</Link>
                        )}
                <Hero />
            </div>
            <div className="logo-section">
                <ion-icon name="game-controller"></ion-icon>
                <h1>The Game Zone</h1>
            </div>
            <Games />
            <Footer />
        </>
    )
}

export default Main
