import React from 'react'
import './NavBar.css'
import { useNavigate,Link} from 'react-router-dom'
import { useEffect } from 'react';
import useUserStore from '../../../store/user';

const NavBar = () => {
    const navigate = useNavigate()
    const { token, user, setUser } = useUserStore(state => ({
        token: state.token,
        user: state.user,
        setUser: state.setUser
    }));

    // Fetch user details from the server if logged in
    useEffect(() => {
        const fetchUser = async () => {
            if (!token) return;
            try {
                const res = await fetch(`${process.env.REACT_APP_BASE_URL}/user-detail`, {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });

                if (res.ok) {
                    const data = await res.json();
                    setUser(data, token); // Store user details in Zustand
                } else {
                    console.error("Failed to fetch user details");
                }
            } catch (error) {
                console.error("Error fetching user details:", error);
            }
        };

        fetchUser();
    }, [token, setUser]);

    return (
        <header>
            <div className="logo" onClick={() => {
                navigate('/')
            }}>
                <ion-icon name="game-controller"></ion-icon>
                <div style={{display:"block"}}>
                <p>The Game Zone</p>
                {token ? (
                            <span className="username">Hey, {user.name} ðŸ‘‹</span>
                        ) : (
                            <Link to="/login" className="login-link">Click Here to Login</Link>
                        )}
                </div>

            </div>
            <button onClick={() => {
                navigate('/leaderboard')
            }}
                type="button" id="btn">LEADERBOARD</button>
        </header>
    )
}

export default NavBar
