import NavBar from '../NavBar/NavBar'
import './Login.css'
import GoogleLogin from 'react-google-login'
import trophy from '../../../assets/home/trophy.png'
import { useEffect, useState } from 'react'
import Loader from '../../Loader/Loader'
import useUserStore from '../../../store/user'
import { useNavigate } from 'react-router-dom'

const Login = () => {
    const [data, setData] = useState(null)
    const [loading, setLoading] = useState(false)
    const navigate = useNavigate()
    const setDetails = useUserStore((state) => state.setDetails);

    const signIn = async () => {
        const res = await fetch(`${process.env.REACT_APP_BASE_URL}/login`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(data),
        });

        const res_data = await res.json();
        console.log("✅ Backend Response:", res_data); // Check backend response

        if (!res_data.token) {
            console.error("❌ Token is missing in response!");
            return;
        }

        // Flatten the structure before storing
        setDetails({ user: res_data.user, token: res_data.token });

        setLoading(false);
        if (res.status === 200) {
            navigate("/");
        } else {
            alert("Could Not Sign In");
        }
    };


    useEffect(() => {
        if (data !== null)
            signIn()
    }, [data])

    const handleFailure = (res) => {
        alert(res)
    }

    const handleLogin = (data) => {
        const { email, name, imageUrl, googleId } = data.profileObj
        setData({ email, name, imageUrl, googleId })
        setLoading(true);
    }

    if (loading)
        return <Loader />
    else
        return (
            <>
                <NavBar />
                <div className='google-container'>
                    <h1>Login With Google</h1>
                    <img src={trophy} alt="Winner"></img>
                    <br></br>
                    <GoogleLogin
                        clientId={process.env.REACT_APP_GOOGLE_CLIENT_ID}
                        buttonText="Login With Google"
                        onSuccess={handleLogin}
                        onFailure={handleFailure}
                        cookiePolicy={'single_host_origin'}
                    >
                    </GoogleLogin>
                </div>
            </>
        )
}

export default Login
